import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { StaffService } from './staff-member.service';

@Component({
  selector: 'app-staff-form',
  templateUrl: './staff-member.component.html',
  styleUrls: ['./staff-member.component.css']
})
export class StaffMemberComponent {
  staffForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private staffService: StaffService) {
    this.staffForm = this.formBuilder.group({
      numeroIdentidad: ['', Validators.required],
      nombre: ['', Validators.required],
      apellidos: ['', Validators.required],
      img: ['', Validators.required],
      celular: ['', Validators.required],
      direccion: ['', Validators.required],
      grupoSanguineo: ['', Validators.required],
      nombreAcudiente: ['', Validators.required],
      contactoAcudiente: ['', Validators.required],
      area: ['', Validators.required],
      fechaNacimiento: ['', Validators.required],
      created_at: [new Date()]
    });
  }

  onSubmit(): void {
    const staff = this.staffForm.value;
    this.staffService.addStaff(staff).subscribe(
      () => {
        console.log('Staff member added successfully!');
      },
      (error) => {
        console.error('Error adding staff member:', error);
      }
    );

    console.log(staff);
  }
}
<form [formGroup]="staffForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="numeroIdentidad">Número de identidad:</label>
    <input id="numeroIdentidad" type="number" formControlName="numeroIdentidad">
  </div>

  <div>
    <label for="nombre">Nombre:</label>
    <input id="nombre" type="text" formControlName="nombre">
  </div>

  <div>
    <label for="apellidos">Apellidos:</label>
    <input id="apellidos" type="text" formControlName="apellidos">
  </div>

  <div>
    <label for="img">Imagen:</label>
    <input id="img" type="text" formControlName="img">
  </div>

  <div>
    <label for="celular">Celular:</label>
    <input id="celular" type="text" formControlName="celular">
  </div>

  <div>
    <label for="direccion">Dirección:</label>
    <input id="direccion" type="text" formControlName="direccion">
  </div>

  <div>
    <label for="grupoSanguineo">Grupo sanguíneo:</label>
    <input id="grupoSanguineo" type="text" formControlName="grupoSanguineo">
  </div>

  <div>
    <label for="nombreAcudiente">Nombre del acudiente:</label>
    <input id="nombreAcudiente" type="text" formControlName="nombreAcudiente">
  </div>

  <div>
    <label for="contactoAcudiente">Contacto del acudiente:</label>
    <input id="contactoAcudiente" type="text" formControlName="contactoAcudiente">
  </div>

  <div>
    <label for="area">Área:</label>
    <input id="area" type="text" formControlName="area">
  </div>

  <div>
    <label for="fechaNacimiento">Fecha de nacimiento:</label>
    <input id="fechaNacimiento" type="date" formControlName="fechaNacimiento">

  </div>
<button type="submit">Guardar</button>

</form>
