import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { StaffMember } from './staff-member.model';
import { StaffMemberService } from './staff-member.service';

@Component({
  selector: 'app-staff-member-form',
  templateUrl: './staff-member-form.component.html',
  styleUrls: ['./staff-member-form.component.css']
})
export class StaffMemberFormComponent {

  staffMemberForm: FormGroup;

  constructor(private staffMemberService: StaffMemberService) {
    this.staffMemberForm = new FormGroup({
      'numeroIdentidad': new FormControl(null, Validators.required),
      'nombre': new FormControl(null, Validators.required),
      'apellidos': new FormControl(null, Validators.required),
      'img': new FormControl(null, Validators.required),
      'celular': new FormControl(null, Validators.required),
      'direccion': new FormControl(null, Validators.required),
      'grupoSanguineo': new FormControl(null, Validators.required),
      'nombreAcudiente': new FormControl(null, Validators.required),
      'contactoAcudiente': new FormControl(null, Validators.required),
      'area': new FormControl(null, Validators.required),
      'fechaNacimiento': new FormControl(null, Validators.required),
      'created_at': new FormControl(new Date())
    });
  }

  onSubmit() {
    const staffMember = new StaffMember(
      this.staffMemberForm.value.numeroIdentidad,
      this.staffMemberForm.value.nombre,
      this.staffMemberForm.value.apellidos,
      this.staffMemberForm.value.img,
      this.staffMemberForm.value.celular,
      this.staffMemberForm.value.direccion,
      this.staffMemberForm.value.grupoSanguineo,
      this.staffMemberForm.value.nombreAcudiente,
      this.staffMemberForm.value.contactoAcudiente,
      this.staffMemberForm.value.area,
      this.staffMemberForm.value.fechaNacimiento,
      this.staffMemberForm.value.created_at
    );

    this.staffMemberService.addStaffMember(staffMember).subscribe(
      response => {
        console.log(response);
      },
      error => {
        console.log(error);
      }
    );
    this.staffMemberForm.reset();
  }

}
