import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { StaffService } from './staff.service';

@Component({
  selector: 'app-staff-form',
  templateUrl: './staff-form.component.html',
  styleUrls: ['./staff-form.component.css']
})
export class StaffFormComponent {
  staff = {
    numeroIdentidad: null,
    nombre: '',
    apellidos: '',
    img: '',
    celular: '',
    direccion: '',
    grupoSanguineo: '',
    nombreAcudiente: '',
    contactoAcudiente: '',
    area: '',
    fechaNacimiento: '',
    created_at: new Date()
  };

  constructor(private staffService: StaffService) {
    this.staff = new staff({});
  }

  submitForm(form: NgForm) {
    if (form.valid) {
      this.staff = {
        numeroIdentidad: form.value.numeroIdentidad,
        nombre: form.value.nombre,
        apellidos: form.value.apellidos,
        img: form.value.img,
        celular: form.value.celular,
        direccion: form.value.direccion,
        grupoSanguineo: form.value.grupoSanguineo,
        nombreAcudiente: form.value.nombreAcudiente,
        contactoAcudiente: form.value.contactoAcudiente,
        area: form.value.area,
        fechaNacimiento: form.value.fechaNacimiento,
        created_at: new Date()
      };
      this.staffService.addStaff(this.staff).subscribe(
        response => console.log(response),
        error => console.log(error)
      );
    }
  }

}
<form #staffForm="ngForm" (ngSubmit)="submitForm(staffForm)">
  <label for="numeroIdentidad">Número de Identidad:</label>
  <input type="number" id="numeroIdentidad" name="numeroIdentidad" required [(ngModel)]="staff.numeroIdentidad">

  <label for="nombre">Nombre:</label>
  <input type="text" id="nombre" name="nombre" required [(ngModel)]="staff.nombre">

  <label for="apellidos">Apellidos:</label>
  <input type="text" id="apellidos" name="apellidos" required [(ngModel)]="staff.apellidos">

  <label for="img">Imagen:</label>
  <input type="text" id="img" name="img" [(ngModel)]="staff.img">

  <label for="celular">Número de Celular:</label>
  <input type="text" id="celular" name="celular" [(ngModel)]="staff.celular">

  <label for="direccion">Dirección:</label>
  <input type="text" id="direccion" name="direccion" [(ngModel)]="staff.direccion">

  <label for="grupoSanguineo">Grupo Sanguíneo:</label>
  <input type="text" id="grupoSanguineo" name="grupoSanguineo" [(ngModel)]="staff.grupoSanguineo">

  <label for="nombreAcudiente">Nombre del Acudiente:</label>
  <input type="text" id="nombreAcudiente" name="nombreAcudiente" [(ngModel)]="staff.nombreAcudiente">

  <label for="contactoAcudiente">Contacto del Acudiente:</label>
  <input type="text" id="contactoAcudiente" name="contactoAcudiente" [(ngModel)]="staff.contactoAcudiente">

  <label for="area">Área:</label>
  <input type="text" id="area" name="area" [(ngModel)]="staff.area">

  <label for="fechaNacimiento">Fecha de Nacimiento:</label>
  <input type="date" id="fechaNacimiento" name="fechaNacimiento" [(ngModel)]="staff.fechaNacimiento">

  <button type="submit">Enviar</button>
</form>
