import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class StaffFormComponent {
  staffForm: FormGroup = new FormGroup({});
  constructor(private formBuilder: FormBuilder) {
    this.createForm();
  }

  createForm() {
    this.staffForm = this.formBuilder.group({
      numeroIdentidad: ['', Validators.required],
      nombre: ['', Validators.required],
      apellidos: ['', Validators.required],
      celular: ['', Validators.required],
      direccion: ['', Validators.required],
      grupoSanguineo: ['', Validators.required],
      nombreAcudiente: ['', Validators.required],
      contactoAcudiente: ['', Validators.required],
      area: ['', Validators.required],
      fechaNacimiento: ['', Validators.required],
      created_at: ['', Validators.required]
    });
  }

  onSubmit() {
    // Implementar la lógica para enviar los datos del formulario al servidor
    console.log(this.staffForm.value);
  }
}
